import { doubaoService } from './src/services/doubao.js';

// Tiny 1x1 white pixel base64 jpeg
const dummyBase64 = "data:image/jpeg;base64,";

async function testImg2Img() {
    console.log("=== Testing Doubao Img2Img ===");
    try {
        // We know this throws, we want the error object
        const url = await doubaoService.generateImageFromImage("A cute cat", dummyBase64);
        console.log("SUCCESS");
    } catch (e: any) {
        console.log("FULL ERROR MESSAGE:");
        const msg = e.message;
        for (let i = 0; i < msg.length; i += 100) {
            console.log(msg.substring(i, i + 100));
        }
    }
}

testImg2Img();
