import { doubaoService } from './src/services/doubao.js';

// Tiny 1x1 white pixel base64 jpeg to simulate upload
const dummyBase64 = "data:image/jpeg;base64,";

async function testVideo() {
    console.log("=== Testing Doubao Video Generation ===");
    try {
        console.log("Sending request...");
        // Use a simple prompt and the dummy image
        const url = await doubaoService.generateVideo(dummyBase64, "A cat running in space");
        console.log("SUCCESS! Video URL:", url);
    } catch (e: any) {
        console.error("FAILURE:", e.message);
        if (e.message.includes('400')) {
            console.log("Hint: Check if the model ID is correct and supports the parameters provided.");
        }
    }
}

testVideo();
